const dropDownButton=document.createElement('button');
dropDownButton.textContent="MAIN";
const choice1=document.createElement('button');
choice1.textContent="CHOICE1";
const choice2=document.createElement('button');
choice2.textContent="CHOICE2";
const choice3=document.createElement('button');
choice3.textContent="CHOICE3";
dropDownButton.style.display="flex";
dropDownButton.style.flexDirection="column";
dropDownButton.addEventListener('mouseover',()=>{
  
  dropDownButton.appendChild(choice1);
  dropDownButton.appendChild(choice2);
  dropDownButton.appendChild(choice3);
});
dropDownButton.addEventListener('mouseout',()=>{
    dropDownButton.removeChild(choice1);
    dropDownButton.removeChild(choice2);
    dropDownButton.removeChild(choice3);
    
  });
export default dropDownButton;